mapper.insert:
insert into resume( resume_no, resume_title, hope_income, volun_field, resume_date, self_intro_cont, member_no, resume_img
) values( resume_seq.nextval, #{resume_title, jdbcType=VARCHAR}, #{hope_income, jdbcType=VARCHAR}, #{volun_field, jdbcType=VARCHAR}, SYSDATE, #{self_intro_cont, jdbcType=VARCHAR}, #{member_no, jdbcType=VARCHAR}, #{resume_img, jdbcType=VARCHAR} )
select resume_seq.currval from dual

insert into career( career_no, resume_no, important_ability, join_date, retire_date, career_work_state, career_etp_name, career_field, work_dept, work_rank, work_month, work_location, work_income, responibility_field, retire_reason
)values( #{career_no}, #{resume_no}, #{important_ability, jdbcType=VARCHAR}, #{join_date, jdbcType=VARCHAR}, #{retire_date, jdbcType=VARCHAR}, #{career_work_state, jdbcType=VARCHAR}, #{career_etp_name, jdbcType=VARCHAR}, #{career_field, jdbcType=VARCHAR}, #{work_dept, jdbcType=VARCHAR}, #{work_rank, jdbcType=VARCHAR}, #{work_month, jdbcType=VARCHAR}, #{work_location, jdbcType=VARCHAR}, #{work_income, jdbcType=VARCHAR}, #{responibility_field, jdbcType=VARCHAR}, #{retire_reason, jdbcType=VARCHAR}
)

mapper.update:
update resume
set
resume_img = #{resume_img}
where resume_no = #{resume_no}

update
member_infor
set resume_no = #{resume_no}
where member_no = #{member_no}

update
resume
set resume_open = #{resume_open}
where resume_no = #{resume_no}

mapper.select:
select FILE_PARENT
from file_box
where file_no = #{file_no}

mapper.delete:
delete from file_box
	where file_no = #{file_no}	AND
	member_no = #{member_no}
